	FM.APPROVED, FM.SOURCE_ID, 
	DATE_FORMAT(FM.POST_DATE, '%d.%m.%Y %H:%i:%s') as POST_DATE, 
	FM.POST_MESSAGE, FM.POST_MESSAGE_HTML, FM.POST_MESSAGE_FILTER, 
