,sc.MODULE_ID
,sc.ITEM_ID
,sc.TITLE
,sc.TAGS
,sc.BODY
,sc.PARAM1
,sc.PARAM2
,sc.UPD
,sc.DATE_FROM
,sc.DATE_TO
,sc.URL
,sc.CUSTOM_RANK
,DATE_FORMAT(sc.DATE_CHANGE, '%d.%m.%Y %H:%i:%s') as FULL_DATE_CHANGE
,DATE_FORMAT(sc.DATE_CHANGE, '%d.%m.%Y') as DATE_CHANGE
,sct.SEARCHABLE_CONTENT
,sc.USER_ID
,scsite.URL as SITE_URL
,scsite.SITE_ID
,1 as RANK

				FROM
					
						b_search_content_text sct
						INNER JOIN b_search_content sc ON sc.ID = sct.SEARCH_CONTENT_ID
						INNER JOIN b_search_content_site scsite ON sc.ID = scsite.SEARCH_CONTENT_ID
					
				WHERE
					((
					EXISTS (
						SELECT 1
						FROM b_search_content_right scg
						WHERE sc.ID = scg.SEARCH_CONTENT_ID
						AND scg.GROUP_CODE = 'G2'
					)))
					AND (( (sct.searchable_content LIKE '%90%') ))
					
				AND (
					(
scsite.SITE_ID = 's1')
					AND(
				(
sc.MODULE_ID = 'main'
AND ((sc.URL LIKE '/%') OR (scsite.URL LIKE '/%')))
					OR(
sc.MODULE_ID = 'forum')
					OR(
sc.MODULE_ID = 'iblock'
AND sc.PARAM1 = 'photos')
					OR(
sc.MODULE_ID = 'iblock'
AND sc.PARAM1 = 'news')
					OR(
sc.MODULE_ID = 'iblock'
AND sc.PARAM1 = 'services')
					OR(
sc.MODULE_ID = 'iblock'
AND sc.PARAM1 = 'job')
					OR(
sc.MODULE_ID = 'blog'
AND (sc.PARAM1 IS NULL OR sc.PARAM1 <> 'MICROBLOG')
				))
				)
				 ORDER BY CUSTOM_RANK DESC, RANK DESC, sc.DATE_CHANGE DESC
LIMIT 500;

